import React from 'react';

function Sidebar({ activeScreen, changeScreen, unattendedCount, isOpen, toggleSidebar }) {
  return (
    <aside className={`sidebar ${isOpen ? 'open' : ''}`}>
      <div className="logo-container">
        <span className="logo-text">CRM System</span>
        <button className="menu-toggle" onClick={toggleSidebar}>
          <i className="fas fa-bars"></i>
        </button>
      </div>
      
      <nav className="sidebar-nav">
        <ul className="nav-list">
          <li className={`nav-item ${activeScreen === 'home' ? 'active' : ''}`} 
              onClick={() => changeScreen('home')}
              data-screen="home">
            <i className="fas fa-home"></i>
            <span>Dashboard</span>
          </li>
          
          <li className={`nav-item ${activeScreen === 'prospects' ? 'active' : ''}`} 
              onClick={() => changeScreen('prospects')}
              data-screen="prospects">
            <i className="fas fa-user-plus"></i>
            <span>Prospectos</span>
            {unattendedCount > 0 && (
              <span className="sidebar-badge">{unattendedCount}</span>
            )}
          </li>
          
          <li className={`nav-item ${activeScreen === 'clients' ? 'active' : ''}`} 
              onClick={() => changeScreen('clients')}
              data-screen="clients">
            <i className="fas fa-users"></i>
            <span>Clientes</span>
          </li>
          
          <li className={`nav-item ${activeScreen === 'records' ? 'active' : ''}`} 
              onClick={() => changeScreen('records')}
              data-screen="records">
            <i className="fas fa-folder-open"></i>
            <span>Expedientes</span>
          </li>
        </ul>
      </nav>
    </aside>
  );
}

export default Sidebar;
