import React from 'react';

function Spinner({ show }) {
  if (!show) return null;
  
  return (
    <div className={`spinner-overlay ${show ? 'active' : ''}`}>
      <div className="spinner-container">
        <div className="spinner"></div>
        <p>Cargando...</p>
      </div>
    </div>
  );
}

export default Spinner;
