import React, { useEffect, useState, useRef } from 'react';
import Chart from 'chart.js/auto';

function Dashboard({ prospectos, clientes, expedientes, refreshData }) {
  const [stats, setStats] = useState({
    totalProspectos: 0,
    prospectosSemana: 0,
    porContactar: 0,
    totalClientes: 0,
    clientesNuevos: 0,
    expedientesActivos: 0
  });
  
  // Usar useRef para mantener referencias a los gráficos
  const canalesChartRef = useRef(null);
  const ubicacionesChartRef = useRef(null);

  useEffect(() => {
    // Calcular estadísticas
    if (prospectos) {
      const prospectosList = Object.values(prospectos);
      const now = new Date();
      const oneWeekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      
      // Formatear fecha para comparar (formato: DD/MM/YYYY)
      const formatDate = (dateStr) => {
        if (!dateStr) return null;
        try {
          const parts = dateStr.split(' ')[0].split('/');
          return new Date(`${parts[1]}/${parts[0]}/${parts[2]}`);
        } catch (e) {
          console.error('Error parsing date:', dateStr);
          return null;
        }
      };
      
      setStats({
        totalProspectos: prospectosList.length,
        prospectosSemana: prospectosList.filter(p => {
          if (!p['fecha-prospecto']) return false;
          const fecha = formatDate(p['fecha-prospecto']);
          return fecha && fecha >= oneWeekAgo;
        }).length,
        porContactar: prospectosList.filter(p => !p.contactado).length,
        totalClientes: Object.values(clientes || {}).length,
        clientesNuevos: Object.values(clientes || {}).filter(c => {
          if (!c.fechaAlta) return false;
          const fecha = formatDate(c.fechaAlta);
          return fecha && fecha >= oneWeekAgo;
        }).length,
        expedientesActivos: Object.values(expedientes || {}).filter(
          e => e.estado === 'abierto'
        ).length
      });
    }
  }, [prospectos, clientes, expedientes]);

  // Efecto separado solo para los gráficos
  useEffect(() => {
    if (!prospectos) return;
    
    // Destruir gráficos existentes para evitar duplicados
    if (canalesChartRef.current) {
      canalesChartRef.current.destroy();
    }
    
    if (ubicacionesChartRef.current) {
      ubicacionesChartRef.current.destroy();
    }
    
    // Inicializar nuevos gráficos
    const prospectosList = Object.values(prospectos);
    
    // Datos para gráfico de canales
    const canalesData = {};
    prospectosList.forEach(p => {
      if (p.canal) {
        canalesData[p.canal] = (canalesData[p.canal] || 0) + 1;
      }
    });
    
    // Datos para gráfico de ubicaciones
    const ubicacionesData = {};
    prospectosList.forEach(p => {
      if (p.ubicacion) {
        // Tomar solo la ciudad (antes de la primera coma)
        const ciudad = p.ubicacion.split(',')[0].trim();
        ubicacionesData[ciudad] = (ubicacionesData[ciudad] || 0) + 1;
      }
    });
    
    // Ordenar ubicaciones por cantidad (descendente)
    const sortedUbicaciones = Object.entries(ubicacionesData)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5);
    
    // Crear gráfico de canales
    const ctxCanales = document.getElementById('canalesChart');
    if (ctxCanales) {
      canalesChartRef.current = new Chart(ctxCanales, {
        type: 'doughnut',
        data: {
          labels: Object.keys(canalesData),
          datasets: [{
            data: Object.values(canalesData),
            backgroundColor: [
              '#4e73df', '#1cc88a', '#36b9cc', '#f6c23e', '#e74a3b', '#858796'
            ]
          }]
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          plugins: {
            legend: {
              position: 'bottom'
            }
          }
        }
      });
      
      // Crear gráfico de ubicaciones
      const ctxUbicaciones = document.getElementById('ubicacionesChart');
      if (ctxUbicaciones) {
        ubicacionesChartRef.current = new Chart(ctxUbicaciones, {
          type: 'bar',
          data: {
            labels: sortedUbicaciones.map(item => item[0]),
            datasets: [{
              label: 'Prospectos por ubicación',
              data: sortedUbicaciones.map(item => item[1]),
              backgroundColor: '#4e73df'
            }]
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                display: false
              }
            },
            scales: {
              y: {
                beginAtZero: true,
                ticks: {
                  precision: 0
                }
              }
            }
          }
        });
      }
    }
    
    // Limpiar los gráficos cuando el componente se desmonte
    return () => {
      if (canalesChartRef.current) {
        canalesChartRef.current.destroy();
      }
      if (ubicacionesChartRef.current) {
        ubicacionesChartRef.current.destroy();
      }
    };
  }, [prospectos]); // Solo dependemos de prospectos para los gráficos

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      
      <div className="stats-container">
        <div className="stat-card" onClick={() => window.navigateToProspects()}>
          <div className="stat-icon bg-primary">
            <i className="fas fa-user-plus"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.totalProspectos}</h3>
            <p>Prospectos</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon bg-success">
            <i className="fas fa-calendar-week"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.prospectosSemana}</h3>
            <p>Nuevos esta semana</p>
          </div>
        </div>
        
        <div className="stat-card" onClick={() => window.navigateToProspects()}>
          <div className="stat-icon bg-warning">
            <i className="fas fa-bell"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.porContactar}</h3>
            <p>Por contactar</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon bg-info">
            <i className="fas fa-users"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.totalClientes}</h3>
            <p>Clientes</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon bg-success">
            <i className="fas fa-user-check"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.clientesNuevos}</h3>
            <p>Nuevos clientes</p>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon bg-primary">
            <i className="fas fa-folder-open"></i>
          </div>
          <div className="stat-info">
            <h3>{stats.expedientesActivos}</h3>
            <p>Expedientes activos</p>
          </div>
        </div>
      </div>
      
      <div className="charts-container">
        <div className="chart-card">
          <h3>Origen de prospectos</h3>
          <div className="chart-container">
            <canvas id="canalesChart"></canvas>
          </div>
        </div>
        
        <div className="chart-card">
          <h3>Ubicaciones principales</h3>
          <div className="chart-container">
            <canvas id="ubicacionesChart"></canvas>
          </div>
        </div>
      </div>
      
      <button className="refresh-btn" onClick={refreshData}>
        <i className="fas fa-sync-alt"></i> Actualizar datos
      </button>
    </div>
  );
}

export default Dashboard;
