import React, { useEffect, useState } from 'react';

function Dashboard({ appData }) {
  const [metrics, setMetrics] = useState({
    prospectosTotales: 0,
    clientesActivos: 0,
    expedientesAbiertos: 0
  });

  useEffect(() => {
    if (appData) {
      setMetrics({
        prospectosTotales: Object.keys(appData.prospecto || {}).length,
        clientesActivos: Object.keys(appData.contactosClientes || {}).length,
        expedientesAbiertos: Object.keys(appData.expedienteClientes || {}).length
      });
    }
  }, [appData]);

  return (
    <div id="home-section" className="screen-section active">
      <div className="metrics-container">
        <div className="metric-card purple" id="prospectos-totales">
          <div className="metric-icon">
            <i className="fas fa-user-plus"></i>
          </div>
          <div className="metric-info">
            <h3>Prospectos Totales</h3>
            <div className="metric-value">{metrics.prospectosTotales}</div>
          </div>
        </div>
        
        <div className="metric-card blue" id="clientes-activos">
          <div className="metric-icon">
            <i className="fas fa-users"></i>
          </div>
          <div className="metric-info">
            <h3>Clientes Activos</h3>
            <div className="metric-value">{metrics.clientesActivos}</div>
          </div>
        </div>
        
        <div className="metric-card orange" id="expedientes-abiertos">
          <div className="metric-icon">
            <i className="fas fa-folder-open"></i>
          </div>
          <div className="metric-info">
            <h3>Expedientes Abiertos</h3>
            <div className="metric-value">{metrics.expedientesAbiertos}</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
